import React, { useState } from 'react';
import { GoogleMap, useLoadScript, DirectionsRenderer } from '@react-google-maps/api';

const libraries = ['places'];
const mapContainerStyle = {
  width: '100%',
  height: '100vh',
};
const center = {
  lat: 51.5074,  // 伦敦中心
  lng: -0.1278,
};

function App() {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
    libraries,
  });

  const [directions, setDirections] = useState(null);
  const [origin, setOrigin] = useState('');
  const [destination, setDestination] = useState('');
  const [originAutocomplete, setOriginAutocomplete] = useState(null);
  const [destinationAutocomplete, setDestinationAutocomplete] = useState(null);

  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading Maps...</div>;

  // 初始化自动补全输入框
  const onLoadOrigin = (autocomplete) => {
    setOriginAutocomplete(autocomplete);
  };

  const onLoadDestination = (autocomplete) => {
    setDestinationAutocomplete(autocomplete);
  };

  const handlePlaceChange = () => {
    if (originAutocomplete && destinationAutocomplete) {
      const originPlace = originAutocomplete.getPlace();
      const destinationPlace = destinationAutocomplete.getPlace();

      if (!originPlace || !originPlace.place_id || !destinationPlace || !destinationPlace.place_id) {
        alert("Please select options from the dropdown list.");
        return;
      }

      const directionsService = new window.google.maps.DirectionsService();
      directionsService.route(
        {
          origin: { placeId: originPlace.place_id },
          destination: { placeId: destinationPlace.place_id },
          travelMode: window.google.maps.TravelMode.WALKING,
        },
        (result, status) => {
          if (status === window.google.maps.DirectionsStatus.OK) {
            setDirections(result);
          } else {
            alert("Directions request failed due to " + status);
          }
        }
      );
    }
  };

  return (
    <div>
      <div style={{ padding: '10px' }}>
        <input
          type="text"
          id="origin-input"
          placeholder="Enter origin"
          onChange={(e) => setOrigin(e.target.value)}
          value={origin}
          ref={(ref) => ref && new window.google.maps.places.Autocomplete(ref, { fields: ['place_id'] }).bindTo('bounds', ref)}
          onLoad={onLoadOrigin}
        />
        <input
          type="text"
          id="destination-input"
          placeholder="Enter destination"
          onChange={(e) => setDestination(e.target.value)}
          value={destination}
          ref={(ref) => ref && new window.google.maps.places.Autocomplete(ref, { fields: ['place_id'] }).bindTo('bounds', ref)}
          onLoad={onLoadDestination}
        />
        <button onClick={handlePlaceChange}>Get Route</button>
      </div>
      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        center={center}
        zoom={13}
      >
        {directions && <DirectionsRenderer directions={directions} />}
      </GoogleMap>
    </div>
  );
}

export default App;
